const header=document.querySelector(".main-header"),scrollChange=16,nav=document.querySelector(".main-nav"),navTrigger=document.querySelector(".main-header__menu-trigger");let scrollpos=window.scrollY;function fixHeaderOnTop(){header.classList.add("main-header--fixed")}function freeHeaderOnTop(){header.classList.remove("main-header--fixed")}function changeMenuState(){navTrigger.classList.toggle("main-header__menu-trigger--opened"),nav.classList.toggle("main-nav--opened")}window.addEventListener("scroll",(function(){scrollpos=this.window.scrollY,scrollpos>=16?fixHeaderOnTop():freeHeaderOnTop()})),navTrigger.addEventListener("click",()=>{changeMenuState()}),nav.addEventListener("click",e=>{e.target.classList.contains("main-nav__link")&&changeMenuState()});